  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
}

.content-card h2 {
  color: #BA0C2F;
  margin-bottom: 1rem;
  font-size: 1.3rem;
}

.content-card h3 {
  color: #13322B;
  margin-bottom: 0.5rem;
  margin-top: 1rem;
  font-size: 1.1rem;
}

.content-card p {
  color: #4a5568;
  margin-bottom: 0.5rem;
  line-height: 1.6;
}

.content-card a {
  color: #BA0C2F;
  text-decoration: none;
  font-weight: 500;
}

.content-card a:hover {
  color: #13322B;
  text-decoration: underline;
}

/* Button styling */
.btn {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  text-decoration: none;
  font-weight: 600;
  text-align: center;
  transition: all 0.3s ease;
  border: 2px solid transparent;
  margin-top: 1rem;
}

.btn-primary {
  background-color: #BA0C2F;
  color: white;
}

.btn-primary:hover {
  background-color: #13322B;
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(186, 12, 47, 0.3);
}

.btn-outline {
  background-color: transparent;
  color: #BA0C2F;
  border-color: #BA0C2F;
}

.btn-outline:hover {
  background-color: #BA0C2F;
  color: white;
  transform: translateY(-1px);
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .content-card {
    padding: 1rem;
    margin-bottom: 1rem;
  }

  .map-container iframe {
    height: 250px;
  }

  .btn {
    width: 100%;
    padding: 1rem;
  }

  /* Stack grid on mobile */
  .grid {
    display: block !important;
  }

  .g-col-md-8,
  .g-col-md-4 {
    width: 100% !important;
  }
}
</style>
```

